import React, { useEffect, useState } from "react";
import Cookies from "js-cookie";
import moment from "moment";
import Header from "./home/Header";
import axios from "axios";

function Profile() {
  const user_id = Cookies.get("user-id");
  const [follow_num, setFollowing_num] = useState({
    following_id: 0,
    followed_id: 0,
  });

  useEffect(() => {
    (async () => {
      console.log("??");
      const following = await axios.get("follow/getFollowingNum", {
        params: { user_id: user_id },
      });
      console.log(following);
      const followed = await axios.get("follow/getFollowedNum", {
        params: { user_id: user_id },
      });
      setFollowing_num({
        ...follow_num,
        following_id: following,
        followed: followed,
      });
    })();
  }, []);

  const nowTime = moment().format("YYYY-MM-DD HH:mm:ss");
  return (
    <div>
      <Header />
      <div>사용자 id : {user_id}</div>
      <div>{nowTime}</div>
      <div>팔로잉 하는 사람 수 :{follow_num?.following_id} </div>
      <div>팔로우 해주는 사람 수 :{follow_num?.followed_id} </div>
    </div>
  );
}

export default Profile;
