import React from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";

const HashLayout = styled.div`
  display: flex;
  flex-direction: row;
  a {
    margin-right: 5px;
  }
`;

function Card({ number, images, author, date, text, postInfo }) {
  let hashtag = text.split(/(#[^\s]+)/g);
  const navigator = useNavigate();

  const hashtagArr = hashtag.filter((str) => {
    if (str !== "" && str !== " ") {
      return str.includes("#");
    }
    return false;
  });

  const authorClick = (author) => {
    navigator(`/home?text=${author}&type=id`);
    // http://localhost:9080/home?text=2&type=text
  };
  const hashtagClick = (str) => {
    let text = str.slice(1);
    navigator(`/home?text=${text}&type=hashtag`);
  };
  const editClick = () => {
    navigator("../edit", {
      state: {
        number: number,
        images: images,
        id: author,
        contents: text,
      },
    });
  };

  return (
    <CardWrapper>
      <img
        src={`http://localhost:8030/write/test?image=${images[0]?.img1}`}
        alt="alt"
        height="250px"
        wisdth="250px"
      />
      <button
        className="tag"
        onClick={() => {
          authorClick(author);
        }}
      >
        글쓴이: {author}
      </button>
      <p>글내용: {text}</p>
      <HashLayout>
        {hashtagArr.map((str) => (
          <button
            className="tag"
            onClick={() => {
              hashtagClick(str);
            }}
          >
            {str}
          </button>
        ))}
      </HashLayout>
      <p>작성시간: {date}</p>
      <button onClick={editClick}>편집</button>
    </CardWrapper>
  );
}

const CardWrapper = styled.div`
  width: 300px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  & > image {
    width: 150px;
    height: 150px;
  }
  .tag {
    border: 0;
    outline: 0;
    background: none;
    color: blue;
  }
`;

export default Card;
