import { React, useEffect, useState } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import Header from "./Header";
import HomeContent from "./HomeContet";
import axios from "axios";
import { Pagination } from "react-bootstrap";

const SearchCheck = styled.div`
  width: 100%;
  margin-top: 50px;
  display: flex;
  flex-direction: row;
  justify-content: center;
  input {
    height: 40px;
    width: 200px;
    border-radius: 3px;
  }
  button {
    height: 40px;
    border-radius: 3px;
  }
  div {
    height: 40px;
  }
`;
const RadioButton = styled.div`
  width: 100%;
  height: 30px;
  display: flex;
  flex-direction: row;
  justify-content: center;
  label {
    padding: 5px;
  }
`;

function Home() {
  const [search, setSearch] = useState({
    text: "",
    type: "",
  });
  const [cardList, setCardList] = useState([]);

  const [page, setPage] = useState(1);
  const [totalPage, setTotalPage] = useState(0);

  useEffect(() => {
    (async () => {
      //get total
      const total = await axios.get("write/totalPage");
      console.log(total);
      setTotalPage(total);
      //data fetch get post
      const result = [];
      //images
      const image = [];

      const imageResult = result.map((item) => {
        const images = image.filter((img) => img.number === item.number);
        return {
          ...item,
          images,
        };
      });
      setCardList(imageResult);
    })();
  }, [page, search]);

  const onRadioClick = (e) => {
    setSearch({
      ...search,
      type: e.target.value,
    });
  };

  const onSearchTextChange = (e) => {
    setSearch({
      ...search,
      text: e.target.value,
    });
  };

  const submitClick = async (e) => {
    const result = await axios.post("/search", { params: search });
  };

  return (
    <div>
      <Header />
      <SearchCheck>
        <input placeholder="검색어" type="text" onChange={onSearchTextChange} />
        <button type="submit" onChange={submitClick}>
          검색
        </button>
      </SearchCheck>
      <RadioButton>
        <label>
          <input type="radio" name="text" value="id" onClick={onRadioClick} />{" "}
          작성자
        </label>
        <label>
          <input type="radio" name="text" value="text" onClick={onRadioClick} />{" "}
          일반텍스트
        </label>
        <label>
          <input
            type="radio"
            name="text"
            value="hashtag"
            onClick={onRadioClick}
          />{" "}
          Hashtag
        </label>
      </RadioButton>
      <HomeContent CardList={cardList} />
      <Pagination>
        {[...Array(totalPage).keys()].map((number) => (
          <Pagination.Item
            active={page === number}
            onClick={() => setPage(number)}
          >
            {number + 1}
          </Pagination.Item>
        ))}
      </Pagination>
    </div>
  );
}

export default Home;
