import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import { PointColor, PrimaryColor } from "../../assets/Color/Color";
import singupPanelImage from "../../assets/Images/signupImage.png";
// import { userApi } from "../../api/index.js";

const SignupPanel = styled.div`
  background-image: url(${singupPanelImage});
  background-size: 100% 100%;
  background-repeat: no-repeat;

  @media screen and (max-width: 1024px) {
    width: 100%;
  }
`;

const SingupLayout = styled.div`
  height: calc(100vh-51px);
  display: flex;
  flex-direction: column;
  align-items: center;
  padding-top: 20px;
  font-family: "YonnGodic340";
  margin-bottom: 50px;
  h1 {
    padding-top: 30px;
    font-size: 58px;
    line-height: 0;
  }
  p {
    font-size: 15px;
    line-height: 20px;
    color: gray;
  }

  @media screen and (max-width: 1024px) {
    width: 100%;
  }

  @media screen and (max-width: 480px) {
    h1 {
      padding-top: 30px;
      font-size: 45px;
      line-height: 0;
    }
    p {
      font-size: 13px;
      line-height: 20px;
      color: gray;
    }
  }

  @media screen and (max-width: 400px) {
    p {
      font-size: 10px;
      line-height: 20px;
      color: gray;
    }
  }
`;

const LoginButton = styled.button`
  margin-top: 30px;
  padding: 10px;
  width: 554px;
  background-color: ${PointColor};
  font-family: "YonnGodic340";
  color: white;
  border-radius: 3px;
  border: 1px solid ${PointColor};

  @media screen and (max-width: 1024px) {
    width: 80%;
  }
`;

const InputForm = styled.div`
  width: 554px;
  display: flex;
  background: white;
  flex-direction: column;
  input {
    outline: gray;
    border: none;
    background: none;
  }
  label {
    line-height: 300%;
  }
  .invalidEmail {
    color: red;
    font-size: 8px;
    line-height: 0;
  }
  .validEmail {
    color: green;
    font-size: 8px;
    line-height: 0;
  }
  .validEmail {
    color: green;
    font-size: 8px;
    line-height: 0;
  }
  .invalidPW {
    color: red;
    font-size: 8px;
    line-height: 0;
  }
  .validPW {
    color: green;
    font-size: 8px;
    line-height: 0;
  }
  .invalidYear {
    color: red;
    font-size: 8px;
    line-height: 0;
  }
  .validYear {
    color: green;
    font-size: 8px;
    line-height: 0;
  }
  .sessionSelect {
    border: none;
    width: 100%;
    height: 100%;
  }
  .email {
    margin-top: 1px;
    padding-bottom: 18px;
    padding-left: 10px;
    font-size: 13px;
    display: flex;
    flex-direction: column;
    border: 1px solid gray;
    border-left: ${(props) =>
      props.inputStates.email ? "3px solid" + PrimaryColor : "1px solid gray"};
    color: gray;
  }
  .password {
    padding-bottom: 18px;
    padding-left: 10px;
    font-size: 13px;
    display: flex;
    flex-direction: column;
    border: 1px solid gray;
    border-left: ${(props) =>
      props.inputStates.password
        ? "3px solid" + PrimaryColor
        : " 1px solid gray"};
    color: gray;
  }
  .confirmPassword {
    padding-bottom: 18px;
    padding-left: 10px;
    font-size: 13px;
    display: flex;
    flex-direction: column;
    border: 1px solid gray;
    border-left: ${(props) =>
      props.inputStates.confirmPassword
        ? "3px solid" + PrimaryColor
        : "1px solid gray"};
    color: gray;
  }
  .userName {
    padding-bottom: 18px;
    padding-left: 10px;
    font-size: 13px;
    display: flex;
    flex-direction: column;
    border: 1px solid gray;
    border-left: ${(props) =>
      props.inputStates.userName
        ? "3px solid" + PrimaryColor
        : "1px solid gray"};
    color: gray;
  }
  .yearSession {
    display: flex;
    flex-direction: row;
  }
  .year {
    padding-bottom: 18px;
    padding-left: 10px;
    font-size: 13px;
    width: 50%;
    display: flex;
    flex-direction: column;
    border: 1px solid gray;
    border-right: none;
    border-left: ${(props) =>
      props.inputStates.year ? "3px solid" + PrimaryColor : "1px solid gray"};
    color: gray;
  }
  .section {
    padding-bottom: 18px;
    padding-left: 10px;
    font-size: 13px;
    width: 50%;
    display: flex;
    flex-direction: column;
    border: 1px solid gray;
    border-left: ${(props) =>
      props.inputStates.section
        ? "3px solid" + PrimaryColor
        : "1px solid gray"};
    color: gray;
  }
  .birthDate {
    padding-bottom: 18px;
    padding-left: 10px;
    font-size: 13px;
    display: flex;
    flex-direction: column;
    border: 1px solid gray;
    border-left: ${(props) =>
      props.inputStates.brithDate
        ? "3px solid" + PrimaryColor
        : "1px solid gray"};
    color: gray;
  }
  .authenticationCode {
    padding-bottom: 18px;
    padding-left: 10px;
    font-size: 13px;
    display: flex;
    flex-direction: column;
    border: 1px solid gray;
    border-left: ${(props) =>
      props.inputStates.authenticationCode
        ? "3px solid" + PrimaryColor
        : "1px solid gray"};
    color: gray;
  }

  @media screen and (max-width: 1024px) {
    width: 80%;
  }
`;

function LoginSignup() {
  const [isValidPW, setIsValidPW] = useState(false);
  const [isValidEmail, setIsValidEmail] = useState(false);
  const [isValidYear, setIsValidYear] = useState(false);
  const navigate = useNavigate();
  const [loginState, setLoginState] = useState({
    email: "",
    password: "",
    userName: "",
    birthDate: "",
    year: "",
    section: "",
    authenticationCode: "",
  });

  useEffect(() => {
    let emailRule =
      /^[0-9a-zA-Z]([-_.]?[0-9a-zA-Z])*@[0-9a-zA-Z]([-_.]?[0-9a-zA-Z])*.[a-zA-Z]{2,3}$/i;
    //이메일 정규표현식
    if (emailRule.test(loginState.email)) {
      setIsValidEmail(true);
    } else {
      setIsValidEmail(false);
    }

    if (loginState.confirmPassword === loginState.password) {
      setIsValidPW(true);
    } else {
      setIsValidPW(false);
    }

    if (isNaN(loginState.year)) {
      setIsValidYear(false);
    } else {
      setIsValidYear(true);
    }
  }, [
    loginState.password,
    loginState.confirmPassword,
    loginState.email,
    loginState.year,
  ]);

  const onChange = (e) => {
    const { value, name } = e.target;
    setLoginState({
      ...loginState,
      [name]: value,
    });
  };

  const [inputStates, setInputStates] = useState({
    email: false,
    password: false,
    confirmPassword: false,
    userName: false,
    brithDate: false,
    year: false,
    section: false,
    authenticationCode: false,
  });

  const clickJoin = () => {
    // userApi.Signup(loginState).then(() => {
    //   navigate.push("/");
    // });
    navigate.push("/");
  };
  const onClick = (inputType) => {
    switch (inputType) {
      case "email":
        setInputStates({
          email: true,
        });
        break;
      case "password":
        setInputStates({
          password: true,
        });
        break;
      case "confirmPassword":
        setInputStates({
          confirmPassword: true,
        });
        break;
      case "userName":
        setInputStates({
          userName: true,
        });
        break;
      case "year":
        setInputStates({
          year: true,
        });
        break;
      case "section":
        setInputStates({
          section: true,
        });
        break;
      case "brithDate":
        setInputStates({
          brithDate: true,
        });
        break;
      case "authenticationCode":
        setInputStates({
          authenticationCode: true,
        });
        break;
      default:
    }
  };

  return (
    <SignupPanel>
      <SingupLayout>
        <h1>BOAZ 회원가입</h1>
        <p>동아리 회원만 회원 가입이 가능하며 인증 코드가 필요합니다.</p>
        <InputForm inputStates={inputStates}>
          <label className="email" onClick={() => onClick("email")}>
            이메일주소
            <input
              type="email"
              name="email"
              onChange={onChange}
              onBlur={() => {
                setInputStates({ ...inputStates, email: false });
              }}
            />
            {loginState.email && (
              <p className={isValidEmail ? "validEmail" : "invalidEmail"}>
                {isValidEmail ? "이메일 양식 OK" : "이메일 양식에 맞춰주세요"}
              </p>
            )}
          </label>

          <label className="password" onClick={() => onClick("password")}>
            비밀번호
            <input
              type="password"
              name="password"
              onChange={onChange}
              onBlur={() => {
                setInputStates({ ...inputStates, password: false });
              }}
            />
          </label>

          <label
            className="confirmPassword"
            onClick={() => onClick("confirmPassword")}
          >
            비밀번호 확인
            <input
              type="password"
              name="confirmPassword"
              onChange={onChange}
              onBlur={() => {
                setInputStates({ ...inputStates, confirmPassword: false });
              }}
            />
            {loginState.confirmPassword && (
              <p className={isValidPW ? "validPW" : "invalidPW"}>
                {isValidPW ? "✅비밀번호 일치" : "❌불일치"}
              </p>
            )}
          </label>

          <label className="userName" onClick={() => onClick("userName")}>
            이름
            <input
              type="text"
              name="userName"
              onChange={onChange}
              onBlur={() => {
                setInputStates({ ...inputStates, userName: false });
              }}
            />
          </label>

          <label className="birthDate" onClick={() => onClick("brithDate")}>
            생년월일
            <input
              type="date"
              name="birthDate"
              onChange={onChange}
              onBlur={() => {
                setInputStates({ ...inputStates, brithDate: false });
              }}
            />
          </label>

          <div className="yearSession">
            <label className="year" onClick={() => onClick("year")}>
              기수
              <input
                type="text"
                name="year"
                onChange={onChange}
                onBlur={() => {
                  setInputStates({ ...inputStates, year: false });
                }}
              />
              {loginState.year && (
                <p className={isValidYear ? "validYear" : "invalidYear"}>
                  {isValidYear ? "✅" : "❌해당 기수 숫자만 입력"}
                </p>
              )}
            </label>

            <label className="section" onClick={() => onClick("section")}>
              세션
              <select
                className="sessionSelect"
                name="section"
                onChange={onChange}
                onBlur={() => {
                  setInputStates({ ...inputStates, section: false });
                }}
              >
                <option value="0">세션을 선택하세요</option>
                <option value="1">분석</option>
                <option value="2">시각화</option>
                <option value="3">엔지니어링</option>
              </select>
            </label>
          </div>

          <label
            className="authenticationCode"
            onClick={() => onClick("authenticationCode")}
          >
            인증코드
            <input
              type="text"
              name="authenticationCode"
              onChange={onChange}
              onBlur={() => {
                setInputStates({ ...inputStates, authenticationCode: false });
              }}
            />
          </label>
        </InputForm>
        <LoginButton onClick={clickJoin}>회원가입 신청하기</LoginButton>
      </SingupLayout>
    </SignupPanel>
  );
}

export default LoginSignup;
