import { React, useState } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import Header from "./Header";
import HomeContent from "./HomeContet";

const SearchCheck = styled.div`
  width: 100%;
  margin-top: 50px;
  display: flex;
  flex-direction: row;
  justify-content: center;
  input {
    height: 40px;
    width: 200px;
    border-radius: 3px;
  }
  button {
    height: 40px;
    border-radius: 3px;
  }
  div {
    height: 40px;
  }
`;
const RadioButton = styled.div`
  width: 100%;
  height: 30px;
  display: flex;
  flex-direction: row;
  justify-content: center;
  label {
    padding: 5px;
  }
`;

function Home() {
  const [search, setSearch] = useState({
    text: "",
    type: "",
  });

  const onRadioClick = (e) => {
    setSearch({
      ...search,
      type: e.target.value,
    });
  };
  const onSearchTextChange = (e) => {
    setSearch({
      ...search,
      text: e.target.value,
    });
  };
  return (
    <div>
      <Header />
      <SearchCheck>
        <input placeholder="검색어" type="text" onChange={onSearchTextChange} />
        <button>검색</button>
      </SearchCheck>
      <RadioButton>
        <label>
          <input type="radio" name="text" value="id" onClick={onRadioClick} />{" "}
          작성자
        </label>
        <label>
          <input type="radio" name="text" value="text" onClick={onRadioClick} />{" "}
          일반텍스트
        </label>
        <label>
          <input
            type="radio"
            name="text"
            value="hashtag"
            onClick={onRadioClick}
          />{" "}
          Hashtag
        </label>
      </RadioButton>
      <HomeContent />
    </div>
  );
}

export default Home;
