import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import { PointColor, PrimaryColor } from "../../Color/Color";
import axios from "axios";
// import { userApi } from "../../api/index.js";

const SignupPanel = styled.div`
  background-size: 100% 100%;
  background-repeat: no-repeat;

  @media screen and (max-width: 1024px) {
    width: 100%;
  }
`;

const SingupLayout = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding-top: 20px;
  margin-bottom: 50px;
  h1 {
    padding-top: 30px;
    font-size: 30px;
    line-height: 0;
  }
  p {
    font-size: 15px;
    line-height: 20px;
    color: gray;
  }

  @media screen and (max-width: 1024px) {
    width: 100%;
  }

  @media screen and (max-width: 480px) {
    h1 {
      padding-top: 30px;
      font-size: 45px;
      line-height: 0;
    }
    p {
      font-size: 13px;
      line-height: 20px;
      color: gray;
    }
  }

  @media screen and (max-width: 400px) {
    p {
      font-size: 10px;
      line-height: 20px;
      color: gray;
    }
  }
`;

const LoginButton = styled.button`
  margin-top: 30px;
  padding: 10px;
  width: 554px;
  background-color: ${PointColor};
  color: white;
  border-radius: 3px;
  border: 1px solid ${PointColor};

  @media screen and (max-width: 1024px) {
    width: 80%;
  }
`;

const InputForm = styled.div`
  width: 554px;
  display: flex;
  background: white;
  flex-direction: column;
  input {
    outline: gray;
    border: none;
    background: none;
  }
  label {
    line-height: 300%;
  }
  .invalidEmail {
    color: red;
    font-size: 8px;
    line-height: 0;
  }
  .validEmail {
    color: green;
    font-size: 8px;
    line-height: 0;
  }
  .validEmail {
    color: green;
    font-size: 8px;
    line-height: 0;
  }
  .invalidPW {
    color: red;
    font-size: 8px;
    line-height: 0;
  }
  .validPW {
    color: green;
    font-size: 8px;
    line-height: 0;
  }
  .email {
    margin-top: 1px;
    padding-bottom: 18px;
    padding-left: 10px;
    font-size: 13px;
    display: flex;
    flex-direction: column;
    border: 1px solid gray;
    border-left: ${(props) =>
      props.inputStates.email ? "3px solid" + PrimaryColor : "1px solid gray"};
    color: gray;
  }
  .password {
    padding-bottom: 18px;
    padding-left: 10px;
    font-size: 13px;
    display: flex;
    flex-direction: column;
    border: 1px solid gray;
    border-left: ${(props) =>
      props.inputStates.password
        ? "3px solid" + PrimaryColor
        : " 1px solid gray"};
    color: gray;
  }
  .confirmPassword {
    padding-bottom: 18px;
    padding-left: 10px;
    font-size: 13px;
    display: flex;
    flex-direction: column;
    border: 1px solid gray;
    border-left: ${(props) =>
      props.inputStates.confirmPassword
        ? "3px solid" + PrimaryColor
        : "1px solid gray"};
    color: gray;
  }
  .userName {
    padding-bottom: 18px;
    padding-left: 10px;
    font-size: 13px;
    display: flex;
    flex-direction: column;
    border: 1px solid gray;
    border-left: ${(props) =>
      props.inputStates.userName
        ? "3px solid" + PrimaryColor
        : "1px solid gray"};
    color: gray;
  }
  .id {
    padding-bottom: 18px;
    padding-left: 10px;
    font-size: 13px;
    display: flex;
    flex-direction: column;
    border: 1px solid gray;
    border-left: ${(props) =>
      props.inputStates.id ? "3px solid" + PrimaryColor : "1px solid gray"};
    color: gray;
  }

  @media screen and (max-width: 1024px) {
    width: 80%;
  }
`;

function LoginSignup() {
  const [isValidPW, setIsValidPW] = useState(false);
  const [isValidEmail, setIsValidEmail] = useState(false);
  const navigate = useNavigate();
  const [loginState, setLoginState] = useState({
    email: "",
    password: "",
    userName: "",
    id: "",
  });

  useEffect(() => {
    let emailRule =
      /^[0-9a-zA-Z]([-_.]?[0-9a-zA-Z])*@[0-9a-zA-Z]([-_.]?[0-9a-zA-Z])*.[a-zA-Z]{2,3}$/i;
    //이메일 정규표현식
    if (emailRule.test(loginState.email)) {
      setIsValidEmail(true);
    } else {
      setIsValidEmail(false);
    }

    if (loginState.confirmPassword === loginState.password) {
      setIsValidPW(true);
    } else {
      setIsValidPW(false);
    }
  }, [
    loginState.password,
    loginState.userName,
    loginState.email,
    loginState.id,
  ]);

  const onChange = (e) => {
    const { value, name } = e.target;
    setLoginState({
      ...loginState,
      [name]: value,
    });
  };

  const [inputStates, setInputStates] = useState({
    email: false,
    password: false,
    confirmPassword: false,
    userName: false,
    id: false,
  });

  const clickJoin = async () => {
    let users = await axios.get("/signup", {
      params: form,
    });
    //navigate("./home")
  };
  const onClick = (inputType) => {
    switch (inputType) {
      case "email":
        setInputStates({
          email: true,
        });
        break;
      case "password":
        setInputStates({
          password: true,
        });
        break;
      case "confirmPassword":
        setInputStates({
          confirmPassword: true,
        });
        break;
      case "userName":
        setInputStates({
          userName: true,
        });
        break;
      case "id":
        setInputStates({
          id: true,
        });
        break;
      default:
    }
  };

  return (
    <SignupPanel>
      <SingupLayout>
        <h1>숭실대학교 Instagram</h1>
        <p>회원가입 신청 후 초기화면으로 이동</p>
        <InputForm inputStates={inputStates}>
          <label className="email" onClick={() => onClick("email")}>
            이메일주소
            <input
              type="email"
              name="email"
              onChange={onChange}
              onBlur={() => {
                setInputStates({ ...inputStates, email: false });
              }}
            />
            {loginState.email && (
              <p className={isValidEmail ? "validEmail" : "invalidEmail"}>
                {isValidEmail ? "이메일 양식 OK" : "이메일 양식에 맞춰주세요"}
              </p>
            )}
          </label>

          <label className="id" onClick={() => onClick("id")}>
            ID
            <input
              type="text"
              name="id"
              onChange={onChange}
              onBlur={() => {
                setInputStates({ ...inputStates, id: false });
              }}
            />
          </label>

          <label className="password" onClick={() => onClick("password")}>
            비밀번호
            <input
              type="password"
              name="password"
              onChange={onChange}
              onBlur={() => {
                setInputStates({ ...inputStates, password: false });
              }}
            />
          </label>

          <label
            className="confirmPassword"
            onClick={() => onClick("confirmPassword")}
          >
            비밀번호 확인
            <input
              type="password"
              name="confirmPassword"
              onChange={onChange}
              onBlur={() => {
                setInputStates({ ...inputStates, confirmPassword: false });
              }}
            />
            {loginState.confirmPassword && (
              <p className={isValidPW ? "validPW" : "invalidPW"}>
                {isValidPW ? "✅비밀번호 일치" : "❌불일치"}
              </p>
            )}
          </label>

          <label className="userName" onClick={() => onClick("userName")}>
            이름
            <input
              type="text"
              name="userName"
              onChange={onChange}
              onBlur={() => {
                setInputStates({ ...inputStates, userName: false });
              }}
            />
          </label>
        </InputForm>
        <LoginButton onClick={clickJoin}>회원가입</LoginButton>
      </SingupLayout>
    </SignupPanel>
  );
}

export default LoginSignup;
