import react from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import { ToggleButtonGroup, ToggleButton } from "react-bootstrap";
import React from "react";
import Header from "./Header";
const SearchCheck = styled.div`
  width: 100%;
  height: 100px;
  display: flex;
  flex-direction: row;
  align-items: center;
  input {
    height: 50px;
  }
`;

function Home() {
  return (
    <div>
      <Header />
      <SearchCheck>
        <input placeholder="검색어" type="text" />
        <button>검색</button>
        <ToggleButtonGroup type="radio" name="options" defaultValue={1}>
          <ToggleButton id="id" value={1}>
            작성자
          </ToggleButton>
          <ToggleButton id="text" value={2}>
            일반텍스트
          </ToggleButton>
          <ToggleButton id="hashtag" value={3}>
            Hashtag
          </ToggleButton>
        </ToggleButtonGroup>
      </SearchCheck>
    </div>
  );
}

export default Home;
