import React, { useState, useRef, useEffect } from "react";
import styled from "styled-components";
import moment from "moment";
import Cookies from "js-cookie";
import axios from "axios";
import { useNavigate, useLocation } from "react-router-dom";
import { Empty } from "antd";

const PostPanel = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  font-size: 20px;
  text-align: center;
  align-items: center;

  .content {
    height: 500px;
    width: 1000px;
  }
  div {
    margin: 10px;
  }
  .exist {
    border: none;
    background-color: 0;
    margin: 5px;
  }
`;

function EditPost() {
  const location = useLocation();
  const id = location.state.id;
  const [imageFilenames, setImage] = useState([]);
  const navigate = useNavigate();
  const images = [];
  const num = location.state.number;
  const [fileLength, setLength] = useState(0);
  const [inputPost, setPost] = useState({
    number: "",
    id: "",
    date: "",
    contents: "",
  });

  useEffect(() => {
    (async () => {
      const temp = location.state.images;
      for (let i in temp[0]) {
        if (temp[0][i] !== null) {
          if (i !== "number") images.push(temp[0][i]);
        }
        setImage(images);
      }

      setPost({
        ...inputPost,
        number: location.state.number,
        id: location.state.id,
        contents: location.state.contents,
      });
    })();
  }, []);

  const formData = new FormData();

  const existFileClick = async (e) => {
    let idx = e.target.id;
    document.getElementById(idx).style.display = "none";
    delete imageFilenames[idx];
    let filtered = imageFilenames.filter(function (el) {
      return el != null;
    });
    setImage(filtered);
    setLength(imageFilenames.length);
  };
  const handleChangeFile = async (e) => {
    if (e.target.files.length > 5 - imageFilenames.length) {
      alert("파일 최대 5개만 선택가능 다시 선택하세요.");
    } else {
      for (let i = 0; i < 5; i++) {
        formData.append("img", e.target.files[i]);
      }
    }
  };

  const handleChangePost = (e) => {
    let date = moment().format("YYYY-MM-DD HH:mm");
    setPost({
      ...inputPost,
      contents: e.target.value,
      id: id,
      date: date,
    });
  };

  const subimtOnClick = async (e) => {
    const config = {
      header: { "content-type": "multipart/form-data" },
    };
    try {
      let data = {
        num: num,
      };
      let result = await axios.post(
        "/write/updateImages",
        formData,
        config
        // JSON.stringify(data)
      );

      await axios({
        method: "post",
        url: "/write/updateImages?text=sd",
        data: {
          formData: formData,
          num: num,
        },
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      console.log(result.data);
      let result2 = await axios.post("/write/updatePost", inputPost);
    } catch (err) {
      alert("지원하지않는 파일존재\n 다른 이미지 파일을 넣어주세요.");
      console.log(err);
    }
    //navigate("/home");
  };

  const deleteOnClick = async () => {
    try {
      let result = await axios.post("/write/deletePost", inputPost);
      navigate("/home");
    } catch {}
  };

  return (
    <PostPanel>
      <input
        className="content"
        placeholder="내용입력"
        type="email"
        value={inputPost.contents}
        onChange={handleChangePost}
      />
      <input
        type="file"
        className="file"
        id="file"
        name="img"
        onChange={handleChangeFile}
        multiple="multiple"
      />
      <div>
        <div>존재하는 파일이름 클릭 시 삭제됨</div>
        현재 존재하는파일
        {imageFilenames.map((fname, idx) => (
          <button
            className="exist"
            key={fname}
            id={idx}
            onClick={existFileClick}
          >
            {fname}
          </button>
        ))}
      </div>

      <button type="submit" onClick={subimtOnClick}>
        수정
      </button>
      <button onClick={deleteOnClick}>게시물 삭제</button>
    </PostPanel>
  );
}

export default EditPost;
