  const handleChangeFile = (e) => {
    const files = e.target.files;
    setImage({ selectedFiles: files });
    console.log(files);
  };

  const nowTime = moment().format("YYYY-MM-DD HH:mm:ss");

  return (
    <div>
      사진선택
      <input
        type="file"
        id="file"
        onChange={handleChangeFile}
        multiple="multiple"
      />
      <input type="text"> 글쓰기</input>
    </div>
  );
}

export default NewPost;
