import React, { useState, useEffect } from "react";
import styled from "styled-components";
import FollowContent from "./FollowContent";
import Header from "../home/Header";
import axios from "axios";
import Cookies from "js-cookie";

const FollowWrapper = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
`;
function Follow() {
  const [followState, setFollowState] = useState({
    id: "",
    following: false,
  });
  const [userState, setUserState] = useState([]);
  const [tableState, setTableState] = useState([]);
  const user_id = Cookies.get("user-id");
  useEffect(() => {
    (async () => {
      const usersRes = await axios.get("follow/user");
      const user = usersRes.data;
      let tableRes = await axios.get("follow/table", {
        params: { user_id: user_id },
      });
      let followList = tableRes.data;
      setUserState(user);
      setTableState(followList);
    })();

    const followingUserList = userState.map((user) => {
      let isFollowedUser = false;
      tableState.forEach((followUser) => {
        if (followUser.followed_id === user.id) isFollowedUser = true;
      });
      return {
        id: user.id,
        following: isFollowedUser,
      };
    });
    console.log("following", followingUserList);
    console.log("user", userState);
    console.log("follow", tableState);
  }, []);

  return (
    <FollowWrapper>
      <Header />
      <FollowContent></FollowContent>
    </FollowWrapper>
  );
}

export default Follow;
