  //  버튼을 클릭 하면 id 값으로 특정하여 가져오게 했다.
  const removeImage = (id) => {
    // 새롭게 스테이트를 변경한다.
    // 매개변수를 주고 매개변수를 필터로 돌려서 아이템을 리턴하는데 id가 맞으면 제외한다.
    setPics((oldState) => oldState.filter((item) => item.id !== id));
  };

