  }, []);
  return (
    <div className="App">
      // 맵함수로 데이터 있는데로 돌린다
      {pics.map((pic) => {
        return (
          <div style={{ marginBottom: "100px" }}>
            // 이미지 데이터의 아이디를 보여주고
            {pic.id}
            <img
              src={pic.imgUrl}
              width="100px"
              height="100px"
              alt="placeholder grey 100px"
            />
            // 버튼을 클릭하면 비동기로 removeImage(pic.id)가 전달되어 작동
