import styled from "styled-components";

const HashLayout = styled.div`
  display: flex;
  flex-direction: row;
  a {
    margin-right: 5px;
  }
`;

function Card({ id, images, author, date, text }) {
  //console.log(images[0].img1);
  let temp = "#밥 #좋아요 안종훈 #헤헤";
  let hashtag = temp.split(/(#[^\s]+)/g);
  const hashtagArr = hashtag.filter((str) => {
    if (str !== "" && str !== " ") {
      return str.includes("#");
    }
    return false;
  });

  console.log(hashtagArr);

  const authorClick = () => {
    //home 리스트 변경
  };
  const hashtagClick = () => {};
  const editClick = () => {
    //편집 기능
  };

  return (
    <CardWrapper>
      <img
        src={`http://localhost:8030/write/test?image=${images[0].img1}`}
        alt="alt"
        height="250px"
        wisdth="250px"
      />
      <button className="tag" onClick={authorClick}>
        글쓴이: {author}
      </button>
      <p>글내용: {text}</p>
      <HashLayout>
        {hashtagArr.map((str) => (
          <button className="tag" onClick={hashtagClick}>
            {str}
          </button>
        ))}
      </HashLayout>
      <p>작성시간: {date}</p>
      <button onClick={editClick}>편집</button>
    </CardWrapper>
  );
}

const CardWrapper = styled.div`
  width: 300px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  & > image {
    width: 150px;
    height: 150px;
  }
  .tag {
    border: 0;
    outline: 0;
    background: none;
    color: aqua;
  }
`;

export default Card;
