import React, { useState, useRef, useEffect } from "react";
import styled from "styled-components";
import moment from "moment";
import Cookies from "js-cookie";
import axios from "axios";
import { useNavigate, useLocation } from "react-router-dom";

const PostPanel = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  font-size: 20px;
  text-align: center;
  align-items: center;

  .content {
    height: 500px;
    width: 1000px;
  }
`;

function EditPost() {
  const location = useLocation();
  const id = location.state.id;
  const [image, setImage] = useState([]);
  const navigate = useNavigate();
  const [inputPost, setPost] = useState({
    id: "",
    date: "",
    contents: "",
  });

  useEffect(() => {
    (async () => {
      const images = location.state.images;
      for (let i in images) {
        console.log("이미지: ", i);
      }
      setPost({
        ...inputPost,
        id: location.state.id,
        contents: location.state.contents,
      });
      setImage({});
    })();
  }, []);
  console.log(inputPost);

  const formData = new FormData();

  const handleChangeFile = async (e) => {
    if (e.target.files.length > 5) {
      alert("파일 최대 5개만 선택가능");
    } else {
      for (let i = 0; i < 5; i++) {
        formData.append("img", e.target.files[i]);
      }
    }
  };

  const handleChangePost = (e) => {
    let date = moment().format("YYYY-MM-DD HH:mm");
    setPost({
      ...inputPost,
      contents: e.target.value,
      id: id,
      date: date,
    });
  };

  const subimtOnClick = async (e) => {
    const config = {
      header: { "content-type": "multipart/form-data" },
    };
    try {
      let result = await axios.post("/write/uploadImages", formData, config);
      let result2 = await axios.post("/write/uploadPost", inputPost);
    } catch (err) {
      alert("지원하지않는 파일존재\n 다른 이미지 파일을 넣어주세요.");
    }
    //navigate("/Home");
  };

  return (
    <PostPanel>
      <input
        className="content"
        placeholder="내용입력"
        type="email"
        onChange={handleChangePost}
      />
      <input
        type="file"
        className="file"
        id="file"
        name="img"
        onChange={handleChangeFile}
        multiple="multiple"
      />
      <button type="submit" onClick={subimtOnClick}>
        전송
      </button>
    </PostPanel>
  );
}

export default EditPost;
