import React, { useState, useRef } from "react";
import styled from "styled-components";
import { PointColor, PrimaryColor } from "../../assets/Color/Color";
import LoginImage from "../../assets/Images/loginImage.png";
import * as api from "../../api/UserApi.js";
import { useHistory } from "react-router-dom";
import { useDispatch } from "react-redux";
// 대문자

const LoginPanel = styled.div`
  height: calc(100%-51px);
  width: 100%;
  display: flex;
  padding-top: 100px;
  padding-bottom: 80px;

  @media screen and (max-width: 1145px) {
    height: 559px;
  }
`;

const LoginInputPanel = styled.div`
  width: 50%;
  height: 100%;
  padding: 70px;
  box-sizing: border-box;

  font-family: "YonnGodic340";
  h1 {
    font-size: 50px;
    line-height: 20px;
  }

  @media screen and (max-width: 1145px) {
    width: 100vw;

    h1 {
      font-size: 50px;
      line-height: 20px;
    }
    p {
      font-size: 15px;
    }

    padding: 60px;
  }

  @media screen and (max-width: 480px) {
    width: 100vw;

    h1 {
      font-size: 30px;
      line-height: 20px;
    }
    p {
      font-size: 10px;
    }

    padding: 60px;
  }

  @media screen and (max-width: 400px) {
    h1 {
      font-size: 18px;
      line-height: 18px;
    }
    p {
      font-size: 8px;
    }

    padding: 40px;
  }
`;

const ImgPanel = styled.div`
  width: 50%;
  background: #f9f9f9;
  background-size: cover;
  display: flex;
  justify-content: center;
  align-items: center;

  @media screen and (max-width: 1145px) {
    display: none;
    width: 0px;
    height: 0px;
  }
`;

const InputOption = styled.div`
  width: 400px;
  margin-top: 5px;
  font-size: 13px;
  font-family: "YonnGodic340";
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  button {
    background: none;
    border: 0ch;
    :hover {
      color: ${PointColor};
    }
  }

  @media screen and (max-width: 1145px) {
    width: 100%;
  }

  @media screen and (max-width: 480px) {
    font-size: 10px;
  }
`;

const LoginButton = styled.button`
  margin-top: 10px;
  padding: 10px;
  width: 400px;
  background-color: ${PointColor};
  font-family: "YonnGodic340";
  color: white;
  border-radius: 3px;
  border: 1px solid ${PointColor};

  @media screen and (max-width: 1145px) {
    width: 100%;
  }
`;

const InputForm = styled.div`
  width: 400px;
  display: flex;
  flex-direction: column;
  input {
    outline: none;
    border: none;
  }
  .email {
    margin-top: 1px;
    padding-bottom: 10px;
    padding-left: 10px;
    font-size: 13px;
    display: flex;
    flex-direction: column;
    border: 1px solid black;
    border-left: ${(props) =>
      props.emailState ? "3px solid" + PrimaryColor : "1px solid black"};
    color: gray;
  }
  .password {
    margin-top: 1px;
    padding-bottom: 10px;
    padding-left: 10px;
    font-size: 13px;
    display: flex;
    flex-direction: column;
    border: 1px solid black;
    border-left: ${(props) =>
      props.passwordState ? "3px solid" + PrimaryColor : " 1px solid black"};
    color: gray;
  }

  @media screen and (max-width: 1145px) {
    width: 100%;
  }
`;
function LoginInput() {
  const [emailState, setEmailState] = useState(false);
  const [passwordState, setPasswordState] = useState(false);
  const emailRef = useRef(null);
  const passwordRef = useRef(null);

  // let history = useHistory();
  // let dispatch = useDispatch();

  const [form, setForm] = useState({
    email: "",
    password: "",
  });

  const onChangeFormData = (e) => {
    const { value, type } = e.target;
    setForm({
      ...form,
      [type]: value,
    });
  };
  const callLogin = (e) => {
    e.preventDefault();
    api.Logoin(dispatch, form.email, form.password).then((data) => {
      if (data.statusCode === 200) {
        history.push("/");
      }
    });
  };

  const callSignUpPage = (e) => {
    history.push("/signup");
  };

  const onClick = (inputType) => {
    if (inputType === "email") {
      emailRef.current.focus();
      setEmailState(true);
      setPasswordState(false);
    } else if (inputType === "password") {
      passwordRef.current.focus();
      setEmailState(false);
      setPasswordState(true);
    }
  };
  return (
    <LoginPanel>
      <LoginInputPanel>
        <h1>BOAZ의 멤버라면,</h1>
        <h1>로그인하세요.</h1>
        <p>동아리 회원만 로그인 혹은 회원가입이 가능합니다.</p>

        <InputForm emailState={emailState} passwordState={passwordState}>
          <div className="email" onClick={() => onClick("email")}>
            이메일주소
            <input
              type="email"
              name="email"
              onChange={onChangeFormData}
              ref={emailRef}
            />
          </div>
          <div className="password" onClick={() => onClick("password")}>
            비밀번호
            <input
              type="password"
              name="password"
              onChange={onChangeFormData}
              ref={passwordRef}
            />
          </div>
        </InputForm>

        <InputOption>
          <label>
            <input className="email" type="checkbox" name="emailSave" /> "Email
            기억해두기"
          </label>
          <div onClick={callSignUpPage} style={{ cursor: "pointer" }}>
            아직 가입하지 않으셨나요?
          </div>
        </InputOption>
        <LoginButton onClick={callLogin}>로그인</LoginButton>
      </LoginInputPanel>

      <ImgPanel>
        <img src={LoginImage} alt="img" />
      </ImgPanel>
    </LoginPanel>
  );
}

export default LoginInput;
